#include <iostream>
void IncrementDirSize(int DirSize, int Val) {
    std::cout << &DirSize << '\n';
    DirSize += Val;
    std::cout << DirSize << " ";
}
int main() {
    int DirSize = 17;
    std::cout << &DirSize << '\n';
    IncrementDirSize(DirSize, 5);
    std::cout << DirSize; //as you see, if you make no edits, it will produce different output.
}
