#include <iostream>

int main() {
    int value = 10;               // a normal int in memory
    int* ptr = &value;            // ptr stores the address of value

    std::cout << "value: " << value << '\n';
    std::cout << "&value: " << &value << '\n';
    std::cout << "ptr:   " << ptr << '\n';
    std::cout << "*ptr:  " << *ptr << '\n';

